import React from 'react';
import './App.css';
import Clarifai from 'clarifai';
import FaceRecognition from './components/FaceRecognition/FaceRecognition';
import Navigation from './components/Navigation/Navigation';
import Logo from './components/Logo/Logo';
import ImageLinkForm from './components/ImageLinkForm/ImageLinkForm';
import Rank from './components/Rank/Rank';
import SignIn from './components/SignIn/SignIn';
import Particles from 'react-particles-js';

const app = new Clarifai.App({
 apiKey: 'e1477ca77afc434d9c60d04f78a73adc'
});


const particlesOptions ={
     particles: {
        number: { 
              value: 50, 
              density: { 
                enable: true, 
                value_area: 800
            }
        }
    }
}



class App extends React.Component {
  constructor(){
    super();
    this.state = {
      input: '',
      imageUrl: '',
      box: {},
      route: 'signin'
    }
  }

   calculateFaceLocation = (response) => {
      const clarifaiFace = response.outputs[0].data.regions[0].region_info.bounding_box;
      const image = document.getElementById('inputImage');
      const width = Number(image.width);
      const height = Number(image.height);
      //console.log(width,height,clarifaiFace);
      return {
        topRow: clarifaiFace.top_row * height, 
        leftCol: clarifaiFace.left_col * width,
        bottomRow: height - (clarifaiFace.bottom_row * height),
        rightCol: width - (clarifaiFace.right_col * width)
        }


  }

  displayFaceBox = (data) =>{
       console.log(data);
       this.setState({box: data})

  }


  onInputChange = (event)=> {
    this.setState({input : event.target.value}); //update input
  }


  onButtonSubmit = (input) =>{
    this.setState({imageUrl : this.state.input}); //update imageUrl from input

    app.models
      .predict(
       "a403429f2ddf4b49b307e318f00e528b",
       this.state.input)//to get the Url input and then response, it is a trap ,should use input here instead of imageURL
      .then(response=>this.displayFaceBox(this.calculateFaceLocation(response)))
      .catch(err=>console.log(err)); 
  }

  onRouteChange = (route)=> {
       this.setState({route: route});
   }
 // outRouteChange = ()=> {
 //       this.setState({route: 'signin'});
 //   }

	render () {
  return (
    <div className="App">
       <Particles className ="particles"
          params={particlesOptions}
       />     
      <Navigation onRouteChange={this.onRouteChange}/>
      <Logo/>
      {this.state.route === 'home'
      ?<div>
          <Rank/>
          <ImageLinkForm 
              onInputChange={this.onInputChange}
              onButtonSubmit={this.onButtonSubmit}/>
          <FaceRecognition box = {this.state.box} 
                          imageUrl={this.state.imageUrl}/> 
        </div>
      : (this.state.route === 'signin'
        ?<SignIn onRouteChange={this.onRouteChange}/>
        :<Register onRouteChange={this.onRouteChange}/>
        )
      } 
    </div>
   );
  } 
}
export default App;
