import React from 'react';




class Register extends React.Component {
  constructor(){
      super();
      this.state = {
        name : '',
        email: '',
        password: ''
      }
    }


  onNameChange = (event)=> {
    this.setState ({name : event.target.value});
  }

  onEmailChange = (event)=> {
    this.setState ({email : event.target.value});
  }

  onPassswordChange = (event)=> {
    this.setState ({password : event.target.value});
  }

  onSubmitSignin = ()=> {
    fetch ('http://localhost:3000/register', {                     //post input of name,email and pwd  to server
      method : 'post',
      headers: {'Content-Type': 'application/json'},
      body : JSON.stringify({
        name : this.state.name,
        email: this.state.email,
        password: this.state.password
      })
    })
    .then(response => response.json())                        
    .then(user =>{                                            //from backend: res.json(database.users[database.users.length-1 will return the latest user
      if (user) {
        this.props.loadUser(user);  
        this.props.onRouteChange('home')
      }
    })
  }


  render() {

    return (
     <article className="br3 ba  b--black-10 mv4 w-100 w-50-m w-25-l mw6 shadow-5 center">
          <main className="pa4 black-80">
            <div className="measure">
              <fieldset id="sign_up" className="ba b--transparent ph0 mh0">
                <legend className="f1 fw6 ph0 mh0">Register</legend>
                <div className="mt3">
                  <label className="db fw6 lh-copy f6" htmlFor="email-address">Name</label>
                  <input 
                      className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                      type="text" 
                      name="name"  
                      id="name"
                      onChange = {this.onNameChange}
                  />
                </div>
                <div className="mt3">
                  <label className="db fw6 lh-copy f6" htmlFor="email-address">Email</label>
                  <input 
                      className="pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                      type="email" 
                      name="email-address"  
                      id="email-address"
                      onChange = {this.onEmailChange}
                  />
                </div>
                <div className="mv3">
                  <label className="db fw6 lh-copy f6" htmlFor="password">Password</label>
                  <input 
                      className="b pa2 input-reset ba bg-transparent hover-bg-black hover-white w-100" 
                      type="password" 
                      name="password"  
                      id="password"
                      onChange = {this.onPassswordChange}
                  />
                </div>

              </fieldset>
              <div className="">
                <input 
                     className="b ph3 pv2 input-reset ba b--black bg-transparent grow pointer f6 dib" 
                     type="submit" 
                     value="Register"
                     onClick= {this.onSubmitSignin} 
                 />
              </div>            
            </div>
          </main>
     </article>
   );
  }

}

export default Register;
